import React, { Component } from 'react';
// hash模式
// import {HashRouter as Router, Link, Route} from 'react-router-dom'

// history模式/后端匹配使用
import {BrowserRouter as Router, Link, Route} from 'react-router-dom'

function Home() {
    return (
        <div>
            <h1>首页</h1>
        </div>
    )
}

function Mine(props) {
    console.log(props)
    return (
        <div>
            <h1>个人中心</h1>
        </div>
    )
}

function Product() {
    return (
        <div>
            <h1>产品</h1>
        </div>
    )
}

function News(props){
    return(
        <div>
            新闻业，新闻id:{props.match.params.id}
        </div>
    )
}

class Luyou extends Component {
    render() { 
        let meObj = {
            pathname:"/mine", // 跳转的路径
            search:"?username=admin", // get请求参数
            hash:"#abc", //设置的HASH值
            state:{msg:'helloworld'} // 传入的数据,可在mine组件中的props参数中获得
        };
        return (  
            <div id="app">
                <Router>
                    <div className="nav">
                        <Link to="/">Home</Link>
                        <Link to="/product">Product</Link>
                        {/* <Link to="/mine">个人中心</Link> */}
                        <Link to={ meObj }>个人中心</Link>

                        <Link to="/news/4567789">动态路由</Link>
                    </div>
                    <Route path="/" exact component={()=>(<h1>首页</h1>)}></Route>
                    <Route path="/product" exact component={Product}></Route>
                    <Route   path="/mine" exact component={Mine}></Route>

                    {/* 动态路由 */}
                    <Route path="/news/:id" component={News}></Route>
                </Router>
            </div>
        );
    }
}
 
export default Luyou;
