import React, { Component } from 'react';
import './style.css'

class Tab extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            newData:null,
            navList:['疫情地图','最新进展','广州疫情','直击现场'],
            tabIndex:0,
            barStyle:{
                left:'22px'
            }
        }
    }
    render() { 
        return (
            <div className="App">
                <div className="nav">
                    {
                        this.state.navList.map((item,index)=>{
                            if(index===this.state.tabIndex){
                                return (
                                    <div key={index} className="navItem active">{item}</div>
                                )
                            }else{
                                return(
                                    <div key={index} className="navItem">{item}</div>
                                )
                            }
                        })
                    }
                </div>

                <div className="bar" style={this.state.barStyle}></div>
            </div>
        );
    }
}
 
export default Tab;
